fields.reduce_func:
function reduce(data) {
    var r = {};

    for (var dk in data) {
        var d = data[dk];
        for (var k in d) {
            if (!r.hasOwnProperty(k)) r[k] = [];
            r[k] = r[k].concat(d[k]);
        }
    }

    return r;
}

fields.map_func:
function map(data) {
    var r = {};
    r[data.length] = [data];
    return r;
};

fields.then_func:
function then(data) {
    var results = "";
    for (var dk in data) {
        results += dk + ": " + data[dk].length + "<br/>";
    }

    var elem = $("#results");
    elem.html(results);
};
